import React from 'react';
import './App.css';

function handleClick(phrase) {
  console.log(phrase);
}
class App extends React.Component {
    render() {
      return (
        <>
        <button onClick={handleClick}>Botão 1</button>
        <button onClick={handleClick('Testando...')}>Botão 2</button>
        <button onClick={handleClick('Opa!')}>Botão 3</button>
        </>
      )
    }
}

export default App;
